package loops;

public class nestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 0;
		for(number = 1; number <=5; number++ )
		{
			System.out.print(number + "   ");
			if (number == 3)
			{
				System.out.print(" 1 ");
			}
		}
	}

}
